import React from 'react'
import Card from '../Components/UI/Card/Card'
import './logsForm.css'
export default function LogsForm() {

    /* 
      当表单项发生变化时候获取用户输入的内容
      
    */

    // 创建三个变量 存储表单数据
    let inputDate = '';
    let inputDesc = '';
    let inputTime = ''

    //   创建一个响应函数，监听日期的变化
    const dateChangeHandler = (e) => {
        // 获取到当前触发事件的对象
        // 事件对象中 保存了当前事件触发时的所有信息
        // e.target 指向的是触发事件的对象
        // console.log(e.target.value)
        inputDate = e.target.value
    }
    //   创建一个响应函数，监听时长的变化
    const timeChangeHandler = (e) => {
        // 获取到当前触发事件的对象
        // 事件对象中 保存了当前事件触发时的所有信息
        // e.target 指向的是触发事件的对象
        // console.log(e.target.value)
        inputTime = e.target.value
    }


    //   创建一个响应函数，监听表单的变化
    /* 
       在react 中  表单不需要自行提交
           而是要通过react提交
    */

    const descChangeHandler = (e) => {
        // 获取到当前触发事件的对象
        // 事件对象中 保存了当前事件触发时的所有信息
        // e.target 指向的是触发事件的对象
        // console.log(e.target.value)
        inputDesc = e.target.value

    }

    // 当表单提交时候 我们要汇总表单数据
    const formSubmitHandler = (e) => {
        e.preventDefault();    //阻止表单的默认行为

        // 获取表单内容
        // 将数据拼装成一个对象
        const newLog = {
            date: new Date(inputDate),
            desc: inputDesc,
            time: +inputTime
        }

    }


    return (
        <Card className='logs-form'>
            <form onSubmit={formSubmitHandler}>
                <div className='form-item' >
                    <label htmlFor='date'>日期</label>
                    <input onChange={dateChangeHandler} id='date' type='date' />
                </div>
                <div className='form-item'>
                    <label htmlFor='desc'>内容</label>
                    <input id='desc' onChange={descChangeHandler} type='text' />
                </div>
                <div className='form-item'>
                    <label htmlFor='time'>时长</label>
                    <input onChange={timeChangeHandler} id='time' type='number' />
                </div>
                <div className='form-btn'>
                    <button>添加</button>
                </div>
            </form>
        </Card>
    )
}
