import './App.css'
import { useState } from 'react'
const App = () => {


    /* 
      state就是一个被state管理的变量
    */

    const result = useState(1)

    const [count, setCount] = result


    const addHandler = () => {
        // 点击后 数字+1
        setCount(count + 1)     //修改state的值
    };



    return <div className={'app'}>
        <h1>{count}</h1>
        <button onClick={addHandler}>1</button>
    </div>
}

export default App
