import './App.css'
const App = () => {
    /* 
     当我们点击+时 数字增大
     点击-时  数字减少
    */
    const addHandler = () => {
        // 点击后 数字+1
        alert('+1')
    };
    const lessHandler = ()=>{
        // 点击后 数字-1
        alert('-1')
    }


    return <div className={'app'}>
        <h1>1</h1>
        <button onclick={addHandler}>+</button>
        <button onclick={lessHandler}>-</button>
    </div>
}

export default App
