import './App.css'
import { useState } from 'react'
const App = () => {


    const [count, setCount] = result


    /* 
     当我们点击+时 数字增大
     点击-时  数字减少
    */
    //    创建一个变量 存储数字
    // let count = 1

    const addHandler = () => {
        // 点击后 数字+1
        setCount(count + 1)     //修改state的值
    };
    const lessHandler = () => {
        // 点击后 数字-1
        setCount(count - 1)     //修改state的值

    }


    return <div className={'app'}>
        <h1>{count}</h1>
        <button onClick={addHandler}>按钮1</button>
    </div>
}

export default App
