import './App.css'
const App = () => {
    /* 
        在react 当组件渲染完毕后 在修改组件中的变量 不会使视图重新渲染
        要使得 组件可以可以收到变量的影响 必须在变量修改后对组件进行重新渲染
    */



    /* 
     当我们点击+时 数字增大
     点击-时  数字减少
    */
    //    创建一个变量 存储数字
    let count = 1

    const addHandler = () => {
        // 点击后 数字+1
        count++
    };
    const lessHandler = () => {
        // 点击后 数字-1
        count--
    }


    return <div className={'app'}>
        <h1>{count}</h1>
        <button onClick={addHandler}>+</button>
        <button onClick={lessHandler}>-</button>
    </div>
}

export default App
