import './App.css'
const App = () => {
    /* 
      react如何获取原生DOM对象
      
    */

    const clickHandler = () => {
        //  通过id获取h1
        const header = document.getElementById('header')
        header.innerHTML = '哈哈哈'

    }

    return <div className={'app'}>
        <h1 id='header'>我是标题</h1>
        <button onClick={clickHandler} >1</button>
        <button >2</button>
    </div>
}

export default App
