import React, { Component } from 'react'

export default class user extends Component {
    /* 
      类组件中的props是存储到类的实例对象的上的
         可以直接通过实例对象访问
           this.props
     
    */


    render() {
       
        return (
            <div>
                <ul>
                    <li>姓名：孙悟空</li>
                    <li>年齡:18</li>
                    <li>性别: 男</li>
                </ul>
            </div>
        )
    }
}
